import React, { useState, useEffect, useRef } from 'react';
//import Socket from './Socket';
import socket from './Socket';

const Chat = ({ nombre }) => {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);

    //useEffect(() => {
    //    socket.emit('CHAT', 'nombre');
    //}, [nombre]);

    useEffect(() => {
        socket.on('CHAT', mensaje => {
            setMessages([...messages, mensaje]);
        })
        return () => {socket.off()}
    }, [messages])

    const submit = (e) => {
        e.preventDefault();
        socket.emit('CHAT', nombre, message)
    }

    return(
        <div>
            <div>
                {messages.map((e, i) => <div key={i}>{e.message}</div>)}
            </div>
            <form onSubmit={submit}>
                <label htmlFor="">Escriba su mensaje</label>
                <textarea name="" id="" cols="30" rows="5" value={message} onChange={e => setMessage(e.target.value)}>

                </textarea>
                <button>Enviar</button>
            </form>
        </div>
    )
}

export default Chat;
