import './App.css';
import io from 'socket.io-client';
/* import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'; */
import MapView from './components/MapView';
import { useState, useEffect } from 'react';
/* import socket from './components/Socket'; */

//const io = require('socket.io-client')
const socket = io('wss://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
    path: "/flights"
});

function App() {

  // aca van los useState
  const [positions, setPositions] = useState([])
  //const [message, setMessage] = useState([])
  //const [messages, setMessages] = useState()
  //const [flights, setFlights] = useState()
  const pos_actual = positions;


  // aca van los useEffect
  //socket.on('POSITION', function(data){
  //  console.log(data);
  //});

  //socket.on('FLIGHTS', function(data){
  //  console.log("Imprimiendo info de flights");
  //  console.log(data);
  //  console.log("Fin info de flights");
  //});

  //socket.on('CHAT');
  useEffect(() => {
    console.log('emitiendo un vuelo');
    socket.emit('FLIGHTS');
    console.log('vuelo emitido');
  }, [] );

  useEffect(() => {
    socket.on('POSITION', vuelo => {
      console.log('nuevas posiciones de vuelos')
      pos_actual[vuelo.code] = vuelo
      setPositions(pos_actual)
      console.log(positions)
    });

    socket.on('FLIGHTS', function(data){
      console.log('imprimiento info del vuelo');
      console.log(data);
      console.log('fin info del vuelo');
    })
  }, [] );


  // aca van las funciones
  //socket.emit('CHAT');
  //socket.emit('FLIGHTS');

  //const handleNewMessage = () => {
  //  console.log('emitiendo mensaje al chat');
  //  socket.emit('CHAT');
  //}

  return(
    <MapView/>
  );

}

export default App;
