import './App.css';
import React from 'react'
import io from 'socket.io-client';
//import MapView from './components/MapView';
import { useState, useEffect } from 'react';
/* import socket from './components/Socket'; */

// imports de MapView.js y Markers.js
import { Map, TileLayer, Marker, Popup, Polyline} from 'react-leaflet';
import 'leaflet/dist/leaflet.css'
import {Airplanes} from './components/Airplanes'

//const io = require('socket.io-client')
const socket = io('wss://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
    path: "/flights"
});

function App() {

  // aca van los useState
  const [positions, setPositions] = useState([]);
  //const [message, setMessage] = useState([]);
  //const [messages, setMessages] = useState();
  const [flights, setFlights] = useState([]);
  const pos_actual = positions;
  const info_vuelos = flights


  // aca van los useEffect
  //socket.on('POSITION', function(data){
  //  console.log(data);
  //});

  //socket.on('FLIGHTS', function(data){
  //  console.log("Imprimiendo info de flights");
  //  console.log(data);
  //  console.log("Fin info de flights");
  //});

  //socket.on('CHAT');
  useEffect(() => {
    console.log('emitiendo un vuelo');
    socket.emit('FLIGHTS');
    console.log('vuelo emitido');
  }, [] );

  useEffect(() => {
    socket.on('POSITION', vuelo => {
      console.log('nuevas posiciones de vuelos');
      //const pos_actual = positions;
      pos_actual[vuelo.code] = vuelo;
      setPositions(pos_actual);
      //console.log(positions)
      console.log(pos_actual);
    });

    socket.on('FLIGHTS', info => {
      console.log('info del vuelo');
      //console.log(info);
      //console.log('fin info del vuelo');
      info_vuelos[info.code] = info;
      setFlights(info_vuelos);
      console.log(info_vuelos);
    });

  }, [] );


  // aca van las funciones
  //socket.emit('CHAT');
  //socket.emit('FLIGHTS');

  //const handleNewMessage = () => {
  //  console.log('emitiendo mensaje al chat');
  //  socket.emit('CHAT');
  //}

/*
  return(
    <MapView/>
  );
*/

  return(
    <Map center={{lat: '51.52437', lng: '13.41053'}} zoom={2}>
      <TileLayer 
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      />
      {Object.values(pos).map((item) => (
          <Marker position={item.position} icon={Airplanes}>
              <Popup>
                  {item.code}
              </Popup>
          </Marker>
      ))}
      {Object.values(flights).map((info) => (
        <Polyline position={[info.origin, info.destination]} color={'red'}></Polyline>
      ))}
    </Map>
  );

};

export default App;
